(window.webpackJsonp=window.webpackJsonp||[]).push([[15],{507:function(t,s,a){"use strict";a.r(s);var n=a(22),e=Object(n.a)({},(function(){var t=this.$createElement,s=this._self._c||t;return s("ContentSlotsDistributor",{attrs:{"slot-key":this.$parent.slotKey}},[s("h1",{attrs:{id:"简介"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#简介"}},[this._v("#")]),this._v(" 简介")]),this._v(" "),s("p",[this._v('Nmap("Network Mapper")是一款用于网络探索和安全审查的免费和开源实用工具。\n很多系统和网络管理员还发现它对网络清单、管理服务升级计划以及监听主机或者服务正常运行时间等任务很有帮助。\nNmap以新颖的方式利用原始的数据包来确定网络上可用的主机，这些主机提供哪些服务（应用名称和版本）,它们正在运行着什么系统（和系统版本），正在使用什么类型的数据包过滤器/防火墙，以及许多其他特征。设计它的目的快速扫描大型网络，但对单个主机工作支持也很好。\nNmap可以在所有主流操作系统上运行，控制台和图形版本都是可用的。')]),this._v(" "),s("p",[this._v("本章通过虚构的故事提供Nmap的一个广泛概述，以及典型的使用方式。\n其中重要的法律条款帮助使用者避免（至少要意识到）有争议的使用方式，可能会导致 ISP（互联网服务提供商）账户被注销，甚至涉及到民事和刑事诉讼。\n它还讨论了远程机器崩溃的风险，以及各种各样的问题，例如Nmap的开源许可证（基于GNU GPL）和版权。")])])}),[],!1,null,null,null);s.default=e.exports}}]);